(1, 1, 'Always', NULL, 'Send Scheduled Mailings', 'Sends out scheduled CiviMail mailings', 'job', 'process_mailing', NULL, 0),
(2, 1, 'Hourly', NULL, 'Fetch Bounces', 'Fetches bounces from mailings and writes them to mailing statistics', 'job', 'fetch_bounces', NULL, 0),
(3, 1, 'Hourly', NULL, 'Process Inbound Emails', 'Inserts activity for a contact or a case by retrieving inbound emails from a mail directory', 'job', 'fetch_activities', NULL, 0),
(4, 1, 'Daily', NULL, 'Process Pledges', 'Updates pledge records and sends out reminders', 'job', 'process_pledge', 'send_reminders=[1 or 0] optional- 1 to send payment reminders', 0),
(5, 1, 'Daily', NULL, 'Geocode and Parse Addresses', 'Retrieves geocodes (lat and long) and / or parses street addresses (populates street number, street name, etc.)', 'job', 'geocode', 'geocoding=[1 or 0] required\nparse=[1 or 0] required\nstart=[contact ID] optional-begin with this contact ID\nend=[contact ID] optional-process contacts with IDs less than this\nthrottle=[1 or 0] optional-1 adds five second sleep', 0),
(6, 1, 'Daily', NULL, 'Update Greetings and Addressees', 'Goes through contact records and updates email and postal greetings, or addressee value', 'job', 'update_greeting', 'ct=[Individual or Household or Organization] required\ngt=[email_greeting or postal_greeting or addressee] required\nforce=[0 or 1] optional-0 update contacts with null value, 1 update all', 0),
(7, 1, 'Daily', NULL, 'Mail Reports', 'Generates and sends out reports via email', 'job', 'mail_report', 'instanceId=[ID of report instance] required\nformat=[csv or print] optional-output CSV or print-friendly HTML, else PDF', 0),
(8, 1, 'Daily', NULL, 'Send Scheduled Reminders', 'Sends out scheduled reminders via email', 'job', 'send_reminder', NULL, 0),
(9, 1, 'Always', NULL, 'Update Participant Statuses', 'Updates pending event participant statuses based on time', 'job', 'process_participant', NULL, 0),
(10, 1, 'Daily', NULL, 'Update Membership Statuses', 'Updates membership statuses. WARNING: Membership renewal reminders have been migrated to the Schedule Reminders functionality, which supports multiple renewal reminders.', 'job', 'process_membership', NULL, 0),
(11, 1, 'Always', NULL, 'Process Survey Respondents', 'Releases reserved survey respondents when they have been reserved for longer than the Release Frequency days specified for that survey.', 'job', 'process_respondent', NULL, 0),
(12, 1, 'Hourly', NULL, 'Clean-up Temporary Data and Files', 'Removes temporary data and files, and clears old data from cache tables. Recommend running this job every hour to help prevent database and file system bloat.', 'job', 'cleanup', NULL, 0),
(13, 1, 'Always', NULL, 'Send Scheduled SMS', 'Sends out scheduled SMS', 'job', 'process_sms', NULL, 0),
